<?php declare(strict_types=1);
use PHPUnit\Framework\Attributes\DataProvider;
use PHPUnit\Framework\Attributes\TestDox;
use PHPUnit\Framework\Attributes\TestWith;
use PHPUnit\Framework\TestCase;
use Symfony\Bundle\FrameworkBundle\Test\WebTestCase;
use Doctrine\ORM\EntityManager;
use Symfony\Bundle\FrameworkBundle\Test\KernelTestCase;
use Symfony\Bundle\FrameworkBundle\KernelBrowser;
use App\Entity\User;
use App\Entity\Topic;
use App\Entity\Post;
use App\Repository\UserRepository;
use App\Repository\TopicRepository;

final class PostControllerTest extends WebTestCase
{
    private UserRepository $repo;
    private static KernelBrowser $client;

    protected function setUp(): void
    {
        self::$client = static::createClient();
        $this->repo = self::getContainer()
            ->get(UserRepository::class);
    }

    protected function tearDown(): void
    {
        parent::tearDown();
    }

    // Assume correct fixtures
    protected function createAuthenticatedClient($uid)
    {
        self::$client->jsonRequest(
            'POST',
            '/api/v1/login_check',
            [
                'email' => "test_case_$uid@email.com",
                'password' => "passwd$uid"
            ]
        );

        $data = json_decode(self::$client->getResponse()->getContent(), true);

        return [
            'CONTENT_TYPE' => 'application/json',
            'HTTP_BEARER' => self::$client->getCookieJar()->get('BEARER')->getValue()
        ];
    }

    #[Test]
    #[DataProvider('dataPostGetIncorrectValuesProvider')]
    #[TestDox('[api/v1/post/get] Trying incorrect request $_dataName')]
    public function testPostGetIncorrectValues(array $rq)
    {
        $crawler = self::$client->jsonRequest('POST', 'api/v1/post/get', $rq);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(400);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[DataProvider('dataPostGetInvalidValuesProvider')]
    #[TestDox('[api/v1/post/get] Trying invalid request $_dataName')]
    public function testPostGetInvalidValues(array $rq)
    {
        $crawler = self::$client->jsonRequest('POST', 'api/v1/post/get', $rq);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(400);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[DataProvider('dataPostGetProvider')]
    #[TestDox('[api/v1/post/get] Trying valid request $_dataName')]
    public function testPostGet(array $rq): void
    {
        $crawler = self::$client->jsonRequest('POST', 'api/v1/post/get', $rq);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(200);
        $this->assertJson($response->getContent());
    }

    // api/v1/post/add
    #[Test]
    #[TestDox('[api/v1/post/add] Trying to use endpoint without authentication')]
    public function testPostAddRequiresJWT(): void
    {
        $crawler = self::$client->request('POST', "/api/v1/post/add");
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(401);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[DataProvider('dataPostAddIncorrectValuesProvider')]
    #[TestDox('[api/v1/post/add] Trying incorrect request: $_dataName')]
    public function testPostAddIncorrectValues(int $uid, array $rq): void
    {
        $headers = static::createAuthenticatedClient($uid);

        $crawler = self::$client->jsonRequest('POST', "/api/v1/post/add", $rq, $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(400);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[DataProvider('dataPostAddInvalidValuesProvider')]
    #[TestDox('[api/v1/post/add] Trying invalid request: $_dataName')]
    public function testPostAddInvalidValues(int $uid, array $rq): void
    {
        $headers = static::createAuthenticatedClient($uid);

        $crawler = self::$client->jsonRequest('POST', "/api/v1/post/add", $rq, $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(422);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[TestDox('[api/v1/post/add] Trying to add post')]
    public function testPostAddValidRequest(): void
    {
        $headers = static::createAuthenticatedClient(1);

        $crawler = self::$client->jsonRequest('POST', "/api/v1/post/add", ['tid' => 1, 'title' => 'title', 'content' => 'content'], $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(201);
        $this->assertJson($response->getContent());
    }



    // api/v1/post/edit
    #[Test]
    #[TestDox('[api/v1/post/edit] Trying to use endpoint without authentication')]
    public function testPostEditRequiresJWT(): void
    {
        $crawler = self::$client->request('PATCH', "/api/v1/post/edit");
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(401);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[DataProvider('dataPostEditIncorrectValuesProvider')]
    #[TestDox('[api/v1/post/edit] Trying incorrect request: $_dataName')]
    public function testPostEditIncorrectValues(int $uid, array $rq): void
    {
        $headers = static::createAuthenticatedClient($uid);

        $crawler = self::$client->jsonRequest('PATCH', "/api/v1/post/edit", $rq, $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(400);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[DataProvider('dataPostEditInvalidValuesProvider')]
    #[TestDox('[api/v1/post/edit] Trying invalid request: $_dataName')]
    public function testPostEditInvalidValues(int $uid, array $rq): void
    {
        $headers = static::createAuthenticatedClient($uid);

        $crawler = self::$client->jsonRequest('PATCH', "/api/v1/post/edit", $rq, $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(422);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[TestDox('[api/v1/post/add] Trying to edit other user post')]
    public function testPostEditOtherUserPostRequest(): void
    {
        $headers = static::createAuthenticatedClient(1);

        $crawler = self::$client->jsonRequest('PATCH', "/api/v1/post/edit", ['pid' => 1, 'content' => 'content'], $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(400);
        $this->assertJson($response->getContent());
    }

    #[Test]
    #[TestDox('[api/v1/post/add] Trying to edit post')]
    public function testPostEditValidRequest(): void
    {
        $headers = static::createAuthenticatedClient(1);

        $crawler = self::$client->jsonRequest('PATCH', "/api/v1/post/edit", ['pid' => 3, 'content' => 'content'], $headers);
        $response = self::$client->getResponse();

        $this->assertResponseStatusCodeSame(200);
        $this->assertJson($response->getContent());
    }




    public static function dataPostGetIncorrectValuesProvider(): array
    {
        $params = [
            "empty" => [],
            "invalid" => ["x" => "1"],
            "only_one1" => ["tid" => 1],
            "only_one2" => ["offset" => 0],
            "only_one3" => ["limit" => 10],
            "only_two1" => ["tid" => 1, "offset" => 0],
            "only_two2" => ["tid" => 1, "limit" => 10],
            "only_two3" => ["offset" => 0, "limit" => 10]
        ];

        array_walk($params, function (array &$item) { $item = array($item); });

        return $params;
    }

    public static function dataPostGetInvalidValuesProvider(): array
    {
        $params = [
            "negative_offset" => ['tid' => 1, 'offset' => -69, 'limit' => 10],
            "negative_limit" => ['tid' => 1, 'offset' => 1, 'limit' => -420],
            "both_invalid" => ['tid' => 1, 'limit' => -1, 'offset' => 0],
            "nonexistant_topic" => ['tid' => 999, 'limit' => 0, 'offset' => 10],
        ];

        array_walk($params, function (array &$item) { $item = array($item); });

        return $params;
    }

    public static function dataPostGetProvider(): array
    {
        $params = [
            "archived" => ['tid' => 1, 'offset' => 1, 'limit' => 10],
            "not_archived" => ['tid' => 2, 'offset' => 1, 'limit' => 10]
        ];

        array_walk($params, function (array &$item) { $item = array($item); });

        return $params;
    }

    public static function dataPostAddIncorrectValuesProvider(): array
    {
        $params = [
            "empty" => [1, []],
            "invalid" => [2, ["x" => "1"]],
            "only_one1" => [3, ["tid" => "1"]],
            "only_one2" => [4, ["title" => "0"]],
            "only_one3" => [5, ["content" => "10"]],
            "only_two1" => [6, ["tid" => "1", "title" => "0"]],
            "only_two2" => [7, ["tid" => "1", "content" => '10']],
            "only_two3" => [8, ["title" => "0", "content" => "10"]]
        ];

        return $params;
    }

    public static function dataPostAddInvalidValuesProvider(): array
    {
        $params = [
            "title_too_long" => [1, [
                "tid" => 1, 
                "title" => "",
                "content" => "content"]],
            "content_too_long" => [1, [
                "tid" => 1,
                "title" => "title",
                "content" => ""
            ]],
        ];

        return $params;
    }

    public static function dataPostEditIncorrectValuesProvider(): array
    {
        $params = [
            "empty" => [1, []],
            "invalid" => [2, ["x" => "1"]],
            "only_one1" => [3, ["pid" => "1"]],
            "only_one2" => [5, ["content" => "10"]],
        ];

        return $params;
    }

    public static function dataPostEditInvalidValuesProvider(): array
    {
        $params = [
            "content_too_long" => [5, [
                "pid" => 11,
                "content" => ""
            ]],
        ];

        return $params;
    }
}